mapper.select:
    select count(log_id)
	from log
    
      
        name like concat('%',#{name},'%')
      
      
        and action = #{action}
      
      
        and detail like concat('%',#{detail},'%')
      
      
        and ip = like concat('%',#{ip},'%')
      
      
	    and create_date >= #{ge_createDate} 
	  
	  
	    and create_date <= #{le_createDate}

    select 
    
	from log
    
      
        name like concat('%',#{name},'%')
      
      
        and action = #{action}
      
      
        and detail like concat('%',#{detail},'%')
      
      
        and ip = like concat('%',#{ip},'%')
      
      
	    and create_date >= #{ge_createDate} 
	  
	  
	    and create_date <= #{le_createDate} 
	  
    
    
      order by ${orderField} ${orderDirection}
    
    
      limit #{startRow}, #{numPerPage}
